export class Modal{constructor(titulo,mensagem){this.titulo=titulo;this.mensagem=mensagem}
criaModal(){const body=document.querySelector('body');const modalSobreposição=document.createElement('div');const modal=document.createElement('div');const titulo=document.createElement('h2');const paragrafo=document.createElement('p');const botao=document.createElement('button');modalSobreposição.classList.add('modal-sobreposicao');modal.classList.add('modal');titulo.innerText=this.titulo;paragrafo.innerText=this.mensagem;botao.innerText='Fechar';botao.classList.add('fechar-model');body.appendChild(modalSobreposição);modalSobreposição.appendChild(modal);modal.appendChild(titulo);modal.appendChild(paragrafo);modal.appendChild(botao)}
ativaModal(){if(!document.querySelector('.modal-sobreposicao'))return
const modalSobreposição=document.querySelector('.modal-sobreposicao');const botao=document.querySelector('.fechar-model');modalSobreposição.classList.add('show');botao.addEventListener('click',e=>{this.desativaModal()})}
desativaModal(){if(!document.querySelector('.show'))return
const modalSobreposição=document.querySelector('.modal-sobreposicao');modalSobreposição.classList.remove('show')}}