const toggle=document.querySelector('#modo-noturno-toggle');const body=document.body;export class ModoEscuro{constructor(temaArmazenado){this.tema=temaArmazenado}
inicia(){if(!this.tema)localStorage.setItem('tema','claro');this.checaTema();this.tema==='claro'?toggle.checked=!0:toggle.checked=!1;this.atualizaTema();this.togglePrecionado()}
checaTema(){this.tema=localStorage.getItem('tema')}
atualizaTema(){if(this.tema==='claro'){body.classList.add('modo-noturno');toggle.setAttribute('aria-label','Ativar modo claro');return}
body.classList.remove('modo-noturno');toggle.setAttribute('aria-label','Ativar modo escuro')}
togglePrecionado(){toggle.addEventListener('change',e=>{toggle.checked?localStorage.setItem('tema','claro'):localStorage.setItem('tema','escuro');this.checaTema();this.atualizaTema()})}}